import React, { Component } from "react";
import Coins from "./components/Coins";
import Pagination from "./components/Pagination";

class App extends Component {
  constructor() {
    super();
    this.state = {
      coins_data: [],
      current_page: 1,
      total_pages: 0,
      coins_per_page: 5,
    };
    this.setCurrentPage
  }

  componentDidMount() {
    fetch("https://swapi.dev/api/people")
      .then((response) => response.json())
      .then((data) =>
        this.setState({
          coins_data: data["results"],
          total_pages: Math.ceil(
            data["results"].length / this.state.coins_per_page
          ),
        })
      );
  }

  setCurrentPage(page_no) {
    this.setState({current_page: page_no})
    // console.log(page_no);
  }

  render() {
    let coins_per_page = this.state.coins_per_page;
    let current_page = this.state.current_page;
    let total_coins = this.state.coins_data.length;
    let last_coin = Math.min(coins_per_page * current_page, total_coins);

    let coin_arr = this.state.coins_data.slice(
      last_coin - coins_per_page,
      last_coin
    );

    return (
      <div>
        {coin_arr.map((val, inx) => {
          return <Coins coin_prop={val} key={inx} />;
        })}
        <ul>
          <Pagination setCurrentPage={this.setCurrentPage} total_pages={this.state.total_pages} />
        </ul>
      </div>
    );
  }
}

export default App;
