import React from "react";
function addPageList(page_arr, st, en, props) {
  for (let i = Math.max(1, st); i <= Math.min(props.total_pages, en); i++) {
    page_arr.push(
      <li
        className="page-item page-link"
        onClick={() => {
          props.setCurrentPage(i);
        }}
      >
        {i}
      </li>
    );
  }
}
function Pagination(props) {
  let page_arr = [];

  if (props.current_page <= 5) {
    //   console.log("jai mata di)")
    addPageList(page_arr, 1, 6, props);
    page_arr.push(<li className="page-item page-link">...</li>);
    addPageList(page_arr, props.total_pages, props.total_pages, props);
  } else {
    addPageList(page_arr, 1, 1, props);
    page_arr.push(<li className="page-item page-link">...</li>);
    addPageList(
      page_arr,
      props.current_page - 2,
      props.current_page + 2,
      props
    );
    if (props.current_page + 3 < props.total_pages) {
      page_arr.push(<li className="page-item page-link">...</li>);
    }
    if (props.current_page + 2 < props.total_pages) {
      addPageList(page_arr, props.total_pages, props.total_pages, props);
    }
  }

  return <ul className="pagination">{page_arr}</ul>;
}

export default Pagination;
