import React, { Component } from "react";
import Coins from "./components/Coins";

class App extends Component {
  constructor() {
    super();
    this.state = {
      coins_data: [],
      current_page: 0,
      total_pages: 0,
      coins_per_page: 5,
    };
  }

  componentDidMount() {
    fetch("https://swapi.dev/api/people")
      .then((response) => response.json())
      .then((data) => this.setState({ coins_data: data["results"], total_pages: data["results"].len }));
  }

  render() {
    // console.log(this.state.coins_data.length);
    let coin_arr = this.state.coins_data.map((val, inx) => {
      return <Coins coin_prop={val} key={inx+1} />;
    });

    return <div>{coin_arr}</div>;
  }

}

export default App;
