import React from "react";
function addPageList(page_arr, st, en, total) {
  for (let i = Math.max(1,st); i <= Math.min(total,en); i++) {
    page_arr.push(
      <li
        className="page-item page-link"
        onClick={() => {
          props.setCurrentPage(i + 1);
        }}
      >
        {i + 1}
      </li>
    );
  }
}
function Pagination(props) {
  let page_arr = [];
  let current_page=props.current_page;
  let total_pages= props.total_pages;

  if (current_page <= 5) {
    addPageList(page_arr, 1, 5, total_pages);
    page_arr.push(<li className="page-item page-link">...</li>);
    addPageList(page_arr, total_pages, total_pages);
  }
  else{
      addPageList(page_arr,1,1,)
      addPageList(page_arr,current_page-2, current_page+2);
  }

  return <ul className="pagination">{page_arr}</ul>;
}

export default Pagination;
